import React from 'react';
import imgURL1 from './../src/icon-umberella.png';
import imgURL2 from './../src/icon-wind.png';
import imgURL3 from './../src/icon-compass.png';
import './App.css';

class Condition extends React.Component{
    render(){
        const citem="20%";
        const location="Brisbane";
        const temp="19 c";
        const wind="3 km/h";
        const compass="NE";
        const air="Clear";

        return(
            <main>
                <section class="weather-condition">
                <div class="weather-condition__location">{location}</div>
                <div style={{textAlign: 'center', fontSize: '14px'}}>{air}</div>
                <div class="weather-condition__temp">{temp}</div>
                <div class="weather-condition__desc">
                    <div>
                        <img src={imgURL1} alt="umberella" />
                        <span class="citem">{citem}</span>
                    </div>
                    <div>
                        <img src="icon/icon-wind.png" /> <span class="citem">{wind}</span>
                    </div>
                    <div>
                        <img src="icon/icon-compass.png" /> <span class="citem">{compass}</span>
                    </div>
                </div>
                </section>
            </main>
        );
    }
}

export default Condition;
