import React from 'react';
import './App.css';
import imgURL1 from './../src/icon-umberella.png';
import imgURL2 from './../src/icon-wind.png';
import imgURL3 from './../src/icon-compass.png';


class Condition extends React.Component{
    render(){
        const citem="20%";
        const location="Brisbane";
        const temp="19 c";
        const wind="3 km/h";
        const compass="NE";
        const air="Clear";

        return(
            <section className="weather-condition">
            <div className="weather-condition__location">{location}</div>
            <div style={{textAlign: 'center', fontSize: '14px'}}>{air}</div>
            <div className="weather-condition__temp">{temp}</div>
            <div className="weather-condition__desc">
                <div>
                    <img src={imgURL1} alt="umberella" />
                    <span className="citem">{citem}</span>
                </div>
                <div>
                    <img src={imgURL2} alt="wind" /> <span className="citem">{wind}</span>
                </div>
                <div>
                    <img src={imgURL3} alt="compass" /> <span className="citem">{compass}</span>
                </div>
            </div>
            </section>
        );
    }
}

export default Condition;
