import React from 'react';
import './App.css';
import imgURL from './../src/logo.png';
import Nav from './nav';
import Condition from './condition';
import Forecast from './forecast';
import Footer from './footer';
import axios from 'axios';
import {format} from 'date-fns';

class Header extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            forecasts :[],
            limit:5,
            current:{},
            name:''
        }
    }

    changeLimit=limit=>{
        this.setState({limit});
    }

    componentDidMount(){
        axios('https://jr-weather-api.herokuapp.com/api/weather?cc=au&city=brisbane')
        .then(response=>{
            const name = response.data.data.city.name;
            const current = response.data.data.current;
            const forecasts = response.data.data.forecast.slice(0, 10);
            this.setState({current})
            this.setState({name})
            this.setState({forecasts})
        });
    }


    render(){
        const header = "Weather Channel";
        return (
            <div className="weather-channel__container">
                <header>
                    <img className="header__logo" src={imgURL} alt="" />
                    <div className="header__title">{header}</div>
                </header>
                <div>
                    <Nav />
                </div>
                <div>
                    <Condition 
                    current={this.state.current}
                    city={this.state.name}/>
                </div>
                <div>
                    <Forecast 
                    forecasts={this.state.forecasts.slice(0,this.state.limit)}
                    changeLimit={this.changeLimit}
                    limit={this.state.limit}/>
                </div>
                <div>
                    <Footer />
                </div>
            </div>
        );
    }
}

export default Header;
