import React from 'react';
import './App.css';
import ForecastTwo from './forecastTwo';

class Forecast extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            clocks:Array(3).fill(['10:00','13:00','16:00']),
        }
    }

    handleClock(i){
        const clocks = [...this.state.clocks];
        this.setState({clocks});
    }

    renderClocks=(index)=>{
        return <ForecastTwo handleClock={()=>this.handleClock(index)} content={this.state.clocks[index]} />
    }

    render(){
        const itemOne="5 items";
        const itemTwo="10 items";

        return(
            <section className="weather-forecast">
                <div className="forecast__switch">
                    <button className="forecast__switch_0 switch-active">{itemOne}</button>
                    <button className="forecast__switch_1">{itemTwo}</button>
                </div>
                <div>
                    {this.renderClocks(0)}
                </div>
                <div>
                    {this.renderClocks(1)}
                </div>
                <div>
                    {this.renderClocks(2)}
                </div>
            </section>
        )
    }
}


export default Forecast;
