        return this.state.clocks[i]
    }
    

    render(){
            const day="Fri";
            const time="10:00";
            const high="19 c";
            const low="8 c";
        return(
            <div>
              <div className="weather-forecast__row">
                <span className="weather-forecast__day">{day}</span>
                <span className="weather-forecast__icon">
                  <i className="fa fa-clock-o">{this.renderForecast()}</i> 
                </span>
                <span className="weather-forecast__high">{high}</span>
                <span className="weather-forecast__low">{low}</span>
              </div>
            </div>
        )
    }
}

export default ForecastTwo;
